import React from 'react'

const HeaderBottom = () => {
    const allitem=[
        {id:1,title:'All'},
        {id:2,title:'Todays Deal'},
        {id:3,title:'Customer Services'},
        {id:4,title:'Gift cards'},
        {id:5,title:'Registry'},
        {id:6,title:'Sell'},
    ]
  return (
    <div className='w-full px-4 h-[36px] bg-amazon_light text-white flex items-center'>
    <ul className='flex  items-center text-sm gap-3'>{
        allitem.map((item)=>(
            <li className='px-2 h-[80%] flex items-center border border-transparent hover:border-white cursor-pointer duration-100 '>{item.title}</li>
        ))
}</ul>
    </div>
  )
}

export default HeaderBottom
