import React, { useState } from 'react'
import Slider from "react-slick";
import {
    bannerImgOne,bannerImgFive,bannerImgThree,advertizement,bannerImgFour,bannerImgTwo
}from '../assets/index'
const Banner = () => {
    const [activedot, Setactivedot]= useState(0)
    const settings = {
        dots: true,
        infinite: true,
autoplay:true,
       arrows:false,
        slidesToShow: 1,
        slidesToScroll: 1,
        beforeChange:(prev, next)=>{
            Setactivedot(next)
        },
        appendDots: dots => (
            <div
              style={{
             position:"absolute",
             top:"70%",
             left:"45%",
             transform:"translate(-50% -50%)",
             width:"210px"
              }}
            >
              <ul style={{ 
                width:"100%",
                display:"flex",
                alignItems:"center",
                justifyContent:"space-between"

               }}> {dots} </ul>
            </div>
          ),
          customPaging: i => (
            <div
              style={{
                width: "30px",
                color: "blue",
                border: "1px blue solid"
              }}
            >
              {i + 1}
            </div>
          )
      };
  return (
  <div className='w-full'>
      <div className='w-full h-full relative'>
 
 <Slider {...settings}>
   <div>
<img src={advertizement} alt=''/>
   </div>
   <div>
<img src={bannerImgOne} alt=''/>
   </div>
   <div>
<img src={bannerImgFour} alt=''/>
   </div>
   <div>
<img src={bannerImgTwo} alt=''/>
   </div>
   <div>
<img src={bannerImgThree} alt=''/>
   </div>
   <div>
<img src={bannerImgFive} alt=''/>
   </div>
   
   
 </Slider>
</div>
  </div>
  )
}

export default Banner
