import React from 'react'
import { useLoaderData } from 'react-router-dom'

const Products = () => {
const data = useLoaderData()
const productData= data.data;
    console.log(productData)
  return (
    <div className='max-w-screen-2xl mx-auto grid grid-cols-4 gap-10 px-4'>
    {
      productData.map((item)=>(
       <div className='bg-white h-auto border-[1px] border-gray-200 py-6 z-30 hover:border-transparent shadow-none hover:shadow-testShadow duration-200 relative flex flex-col gap-4'>
         <div className='w-full h-auto flex items-center justify-center px-4'>
          <img className='w-52 h-64 object-contain' src={item.image} alt='produtsImg'/>
        </div>
        <div>
          <h2>{item.title}</h2>
          <p>$ {item.price}</p>
        </div>
       </div>
      ))
    }
    </div>
  )
}

export default Products
