import React from 'react'
import { useLoaderData,useDispatch } from 'react-router-dom'
import StarIcon from '@mui/icons-material/Star';
import ApiIcon from '@mui/icons-material/Api';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import ArrowRightIcon from '@mui/icons-material/ArrowRight';
const Products = () => {
  const dispatch=  useDispatch();
  const data = useLoaderData()
  const productData = data.data;
  console.log(productData)
  return (
    <div className='max-w-screen-2xl mx-auto grid  grid-cols-1 md:grid-cols-2 xl:grid-cols-4 xl:gap-10 px-4 gap-6'>
      {
        productData.map((item) => (
          <div key={item.id} className='bg-white h-auto border-[1px] border-gray-200 py-8 z-30 hover:border-transparent shadow-none hover:shadow-testShadow duration-200 relative flex flex-col gap-4 '>
            <div className='w-full h-auto flex items-center justify-center relative group '>
              <img className='w-52 h-64 object-contain' src={item.image} alt='produtsImg' />
              <ul className='w-full h-36 bg-gray-200 absolute bottom-[-170px]   flex flex-col items-end justify-center gap-2 font-titleFont px-2 border-1 border-r group-hover:bottom-0 duration-700\
              '>
                <li className='text-gray-600 hover:text-black text-sm font-medium border-b-[1px] border-b-gray-400 hover:border-b-gray-700 flex items-center justify-end gap-2 hover:cursor-pointer duration-300 w-full'>Compare<span><ApiIcon /></span></li>
                <li className='text-gray-600 hover:text-black text-sm font-medium border-b-[1px] border-b-gray-400 hover:border-b-gray-700 flex items-center justify-end gap-2 hover:cursor-pointer duration-300 w-full'>Add to Cart<span><ShoppingCartIcon /></span></li>
                <li className='text-gray-600 hover:text-black text-sm font-medium border-b-[1px] border-b-gray-400 hover:border-b-gray-700 flex items-center justify-end gap-2 hover:cursor-pointer duration-300 w-full'>View Details<span><ArrowRightIcon /></span></li>
                <li className='text-gray-600 hover:text-black text-sm font-medium border-b-[1px] border-b-gray-400 hover:border-b-gray-700 flex items-center justify-end gap-2 hover:cursor-pointer duration-300 w-full'>Add to WishList<span><FavoriteIcon /></span></li>

              </ul>
              <span className='text-xs capitalize italic absolute top-2 right-2 text-gray-400'>{item.category}</span>
            </div>
            <div className='px-4 z-10 bg-white'>
              <div className='flex items-center justify-between'>
                <h2 className='font-titleFont tracking-wide text-lg text-amazon_blue'>{item.title.substring(0, 20)}</h2>
                <p className='text-sm text-gray-600 font-semibold'>$ {item.price}</p>
              </div>
              <div className=''>
                <p className='text-sm'>{item.description.substring(0, 100)}...</p>
                <div className='text-yellow-500'><StarIcon />
                  <StarIcon />
                  <StarIcon />

                </div>
                <button className='w-full font-titleFont font-medium text-base bg-gradient-to-tr from-yellow-400 to-yellow-200 border hover:from-yellow-300 hover:to-yellow-500 border-yellow-500 hover:border-yellow-700 active:bg-gradient-to-bl active:from-yellow-400 active:to-yellow-500 duration-200 py-1.5 rounded-md mt-3'>Add to Cart</button>
              </div>
            </div>
          </div>
        ))
      }
    </div>
  )
}

export default Products
